import React from 'react'
import axios from 'axios'
import { ToastContainer } from "react-toastr";
import styled from 'styled-components'
import colors from '../app/colors'
// images
import UserSVG from '../assets/images/svg/user-name.svg'
import DirectSVG from '../assets/images/svg/direct.svg'
import PhoneSVG from '../assets/images/svg/phone-call.svg'
import WhatsappSVG from '../assets/images/svg/whatsapp.svg'

export default class Form extends React.Component {

  initialState = {
    name: 'Isac',
    email: 'a@a',
    phone: 'a',
    whatsapp: 'a',
    subjectMatter: 'Apenas entrar em contato',
    message: 'Hello'
  }

  state = { ...this.initialState }

  handleSubmit(e) {

    e.preventDefault()

    this.container.success(`hi! Now is ${new Date()}`, `///title\\\\\\`, {
      closeButton: true,
    })

    console.log(this.state)

    // axios.post('https://www.lfmcontabilidade.com.br/mailer', this.state)
    //   .then(() => {
    //     this.setState(this.initialState)
    //     window.alert('Mensagem enviada com sucesso')
    //   }).catch(console.log)
  }

  render() {
    return (
      <div className='contact' style={{ display: "flex", justifyContent: 'flex-start' }}>
        <ToastContainer
          ref={ref => this.container = ref}
          className="toast-top-right"
        />
        <div className='col-md-12 col-sm-12'>
          <FormRequest onSubmit={e => this.handleSubmit(e)}>
            <FormTitleContainer>
              <h5>Deixe-nos um email</h5>
              <p>Iremos avaliar o seu caso e responde-lo em breve</p>
            </FormTitleContainer>
            <div className="form-row">
              <div className="form-group col-md-6">
                <InputContainer>
                  <UserSVG width={24} height={24} />
                  <input type="text" placeholder="* Nome Completo" required
                    value={this.state.name} onChange={e => this.setState({ name: e.target.value })} />
                </InputContainer>
              </div>
              <div className="form-group col-md-6">
                <InputContainer>
                  <DirectSVG width={24} height={24} />
                  <input type="email" placeholder="* E-mail" required
                    value={this.state.email} onChange={e => this.setState({ email: e.target.value })} />
                </InputContainer>
              </div>
            </div>
            <div className="form-row">
              <div className="form-group col-md-6">
                <InputContainer>
                  <PhoneSVG width={24} height={24} />
                  <input type="text" placeholder="Telefone"
                    value={this.state.phone} onChange={e => this.setState({ phone: e.target.value })} />
                </InputContainer>
              </div>
              <div className="form-group col-md-6">
                <InputContainer>
                  <WhatsappSVG width={24} height={24} />
                  <input type="text" placeholder="Whatsapp"
                    value={this.state.whatsapp} onChange={e => this.setState({ whatsapp: e.target.value })} />
                </InputContainer>
              </div>
            </div>

            <div class="form-group">
              <label for="exampleFormControlSelect1">Assunto</label>
              <InputContainer className="reset-padding-vertical reset-padding-horizontal">
                <select class="form-control" id="exampleFormControlSelect1"
                  value={this.state.subjectMatter} onChange={e => this.setState({ subjectMatter: e.target.value })}>
                  <option>Apenas entrar em contato</option>
                  <option>Desejo abrir minha empresa</option>
                  <option>Fechar empresa</option>
                  <option>Descobrir o melhor formato para minha empresa</option>
                  <option>Quero mudar de contador</option>
                </select>
              </InputContainer>
            </div>

            <div className="form-group">
              <InputContainer>
                <textarea rows="4" required placeholder="* Como podemos te ajudar?" value={this.state.message}></textarea>
              </InputContainer>
            </div>
            <div style={{ display: "flex", justifyContent: "flex-end", marginTop: '30px' }}>
              <button style={{ backgroundColor: colors.primaryColor, padding: '15px 40px', fontSize: '0.85em' }}
                className="btn btn-primary btn-sm mybutton">
                Enviar mensagem
              </button>
            </div>
          </FormRequest>
        </div>
      </div>
    )
  }
}

const FormTitleContainer = styled.div`
  font-family: Montserrat;
  margin-bottom: 70px;
  h5 {
    color: #2f314a;
    font-weight: bold;
    font-size: 1.4em;
  }
  p {
    position: relative;
    font-size: 1.2em;
    font-weight: 400;
    color: #2f314a;
    bottom: 5px;
  }
`
const FormRequest = styled.form`
  background-color: #f7f8ff;
  border-radius: 10px;
  padding: 30px 15px;
  @media(min-width: 600px) {
    padding: 30px 50px;
  }
`
const InputContainer = styled.div`
  display: flex;
  align-items: center;
  background-color: white;
  border: 2px solid #d1d1f3;
  padding: 10px 12px;
  border-radius: 5px;
`
