import React from 'react'
import styled from 'styled-components'
import colors from '../src/app/colors'
import user from '../src/app/user'
// components
import Header from '../src/components/Header'
// import Office from '../components/Office'
// import Services from '../components/Services'
// import About from '../components/About'
// import Contact from '../components/Contact'
// images

export default function Home() {
  return (
    <Container>

      <Header />
      <Office />

      <div className='container-fluid'>
        <div className='row justify-content-end no-glutters'>
          <div className='col-md-7 col-sm-12 no-glutters'>
            <ConquestContainer className='row justify-content-center'>
              <div className='col-md-3 col-sm-12'>
                <ConquestItem>
                  <ConquestName>Fundado em</ConquestName>
                  <ConquestNumber>1996</ConquestNumber>
                </ConquestItem>
              </div>

              <div className='col-md-3 col-sm-12'>
                <ConquestItem>
                  <ConquestName>Anos de experiência</ConquestName>
                  <ConquestNumber style={{ color: '#41caf6' }}>24</ConquestNumber>
                </ConquestItem>
              </div>
            </ConquestContainer>
          </div>
        </div>
      </div>

      <Services />

      <FormTitleContainer>
        <FormTitle>Contato</FormTitle>
        <DecorationLine />
      </FormTitleContainer>

      {/* <div className='container-fluid no-gutters justify-content-center' style={{
        marginBottom: 50,
        backgroundColor: colors.secundaryColor,
        padding: '55px 0px'
      }}>
        <div className='row justify-content-center no-gutters'>
          <div className='col-md-6 col-sm-12'> <About /> </div>
          <div className='col-md-6 col-sm-12'> <Contact /> </div>
        </div>
      </div> */}

      <WhatsappContainer onClick={() => {
        window.open(user.whatsapp, '_blank').focus()
      }} >
        <WhatsappIcon src={require('../assets/icons/whatsapp-logo.png')} />
      </WhatsappContainer>

      <Footer>
        <p style={{fontFamily: 'Montserrat', marginTop: 12, fontWeight: 500}}>© Todos direitos reservados</p>
      </Footer>

    </Container>
  )
}

const ConquestContainer = styled.div`
  background: #21233a;
  font-family: 'Montserrat', sans-serif;
  padding: 25px 0px;
  margin-bottom: 50px;
  @media(min-width: 600px) {
    margin-bottom: 25px;
  }
`
const ConquestItem = styled.div`
  padding: 40px 0px;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  text-align: center;
  margin: 15px 0px;
  @media(min-width: 600px) {
    margin: 0px 0px;
    padding-top: 60px;
  }
`
const ConquestNumber = styled.p`
  /* color: #2f314a; */
  color: #2c81e5;
  font-size: 3.88em;
  font-weight: bold;
`
const ConquestName = styled.h5`
  color: #6b7c93;
  color: white;
  font-size: 1.22em;
  font-weight: bold;
  text-transform: uppercase;
`

//


const Container = styled.div`
`

const FormTitleContainer = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  flex-direction: column;
  margin-bottom: 100px;
  margin-top: 100px;
`

const FormTitle = styled.h5`
  color: #2f314a;
  font-family: 'Montserrat', sans-serif;
  font-weight: bolder;
  font-size: 1.8em;
  text-transform: uppercase;
`

const WhatsappContainer = styled.div`
  background-color: #3abf4c;
  position: fixed;
  z-index: 999999;
  right: 20px;
  bottom: 20px;
  padding: 12px;
  border-radius: 100px;
  display: flex;
  justify-content: center;
  align-items: center;
  box-shadow: 1px 1px 6px rgba(0,0,0,0.4);
  cursor: pointer;
  @media(min-width: 600px) {
    right: 40px;
    bottom: 40px;
    padding: 15px;
  }
`
const WhatsappIcon = styled.img`
  z-index: 999999;
  height: 30px;
  width: 30px;
`
const DecorationLine = styled.div`
  width: 50px;
  height: 3px;
  background: ${colors.primaryColor};
  text-align: center;
  margin: 5px 0px;
`
const Footer = styled.footer`
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: column;
  background-color: white;
  background-size: cover;
  background-position: center; 
  background-repeat: no-repeat;
  width: 100%;
  padding: 25px 0px;
  margin-top: 100px;
  color: #2f314a;
  border-top: 2px solid #d8dfe5;
  /* font-family: 'Bariol', sans-serif; */
`
