import React from 'react'
import axios from 'axios'
import styled from 'styled-components'
import colors from '../app/colors'

export default class Form extends React.Component {

  initialState = {
    name: '',
    email: '',
    location: '',
    whatsappNumber: '',
    message: ''
  }

  state = { ...this.initialState }

  sendForm(e) {
    e.preventDefault();

    axios.post('https://www.lfmcontabilidade.com.br/mailer', this.state)
      .then(() => {
        this.setState(this.initialState)
        window.alert('Mensagem enviada com sucesso')
      }).catch(console.log)
  }

  render() {
    return (
      <div className='contact' style={{ display: "flex", justifyContent: 'flex-start' }}>
        <div className='col-md-12 col-sm-12'>
          <FormRequest onSubmit={(e) => this.sendForm(e)}>
            <FormTitleContainer>
              <h5>Deixe-nos um recado</h5>
              <p>Iremos avaliar o seu caso e responde-lo em breve</p>
            </FormTitleContainer>
            <div className="form-row">
              <div className="form-group col-md-6">
                <label htmlFor="inputName">*Nome</label>
                <input type="text" className="form-control" id="inputName" required
                  value={this.state.name} onChange={(e) => this.setState({ name: e.target.value })} />
              </div>
              <div className="form-group col-md-6">
                <label htmlFor="inputEmail">*Email</label>
                <input type="email" className="form-control" id="inputEmail" required
                  placeholder="seu_email@exemplo.com"
                  value={this.state.email} onChange={(e) => this.setState({ email: e.target.value })} />
              </div>
            </div>
            <div className="form-group">
              <label htmlFor="inputAddress">Local da empresa</label>
              <input type="text" className="form-control" id="inputAddress" placeholder=""
                value={this.state.location} onChange={(e) => this.setState({ location: e.target.value })} />
            </div>
            <div className="form-row">
              <div className="form-group col-md-6">
                <label htmlFor="inputWhatsappNumber">*Whatsapp</label>
                <input type="text" className="form-control" id="inputWhatsappNumber" required
                  value={this.state.whatsappNumber} onChange={(e) => this.setState({ whatsappNumber: e.target.value })} />
              </div>
            </div>
            <div className="form-group">
              <label htmlFor="textAreaMessage">*Como podemos te ajudar?</label>
              <textarea className="form-control" id="textAreaMessage" rows="3"
                required
                value={this.state.message} onChange={(e) => this.setState({ message: e.target.value })} ></textarea>
            </div>
            <div style={{ display: "flex", justifyContent: "flex-end", marginTop: '30px' }}>
              <button style={{ backgroundColor: colors.primaryColor, padding: '15px 40px', fontSize: '0.85em' }}
                className="btn btn-primary btn-sm mybutton">
                Enviar mensagem
              </button>
            </div>
          </FormRequest>
        </div>
      </div>
    )
  }
}

const FormTitleContainer = styled.div`
  font-family: Montserrat;
  margin-bottom: 50px;
  h5 {
    color: #2f314a;
    font-weight: bold;
    font-size: 1.4em;
  }
  p {
    position: relative;
    font-size: 1.2em;
    font-weight: 400;
    color: #2f314a;
  }
`
const FormRequest = styled.form`
  background-color: white;
  border-radius: 10px;
  padding: 30px 15px;
  @media(min-width: 600px) {
    padding: 30px 50px;
  }
`
