import React from 'react'
import axios from 'axios'
import { ToastContainer, toast } from 'react-nextjs-toast'
import styled from 'styled-components'
import colors from '../app/colors'
// images
import UserSVG from '../assets/images/svg/user-name.svg'
import DirectSVG from '../assets/images/svg/direct.svg'
import PhoneSVG from '../assets/images/svg/phone-call.svg'
import WhatsappSVG from '../assets/images/svg/whatsapp.svg'
import CheckMarkSVG from '../assets/images/svg/check-mark.svg'

export default class Form extends React.Component {

  initialState = {
    isValidEmail: false,
    form: {
      name: '',
      email: '',
      phone: '',
      whatsapp: '',
      subjectMatter: 'Apenas entrar em contato',
      message: ''
    }
  }

  state = { ...this.initialState }

  handleSubmit(e) {

    e.preventDefault()

    console.log(this.state)

    axios.post('https://www.lfmcontabilidade.com.br/mailer', this.state.form)
      .then(() => {
        this.setState(this.initialState.form)
        toast.notify('Mensagem enviada', {
          title: "Sucesso!",
          duration: 5,
          type: "success"
        })
      }).catch(() => {
        toast.notify('Algo deu errado, por favor tente enviar novamente mais tarde, pedimos desculpa', {
          title: "Falha",
          duration: 8,
          type: "error"
        })
      })
  }

  handleChangeEmail(e) {

    const validations = [
      this.state.form.email.includes("@"),
      this.state.form.email.includes(".com"),
      this.state.form.email.length > 5
    ]

    this.setState({
      email: e.target.value.trim()
    })

    validations.forEach(isValid => {
      if(isValid)
        this.setState({ isValidEmail: true })
    })
  }

  render() {
    return (
      <div className='contact' style={{ display: "flex", justifyContent: 'flex-start' }}>
        <div className='col-md-12 col-sm-12'>
          <FormRequest onSubmit={e => this.handleSubmit(e)}>
            <FormTitleContainer>
              <h5>Deixe-nos um email</h5>
              <p>Iremos avaliar o seu caso e responde-lo em breve</p>
            </FormTitleContainer>
            <div className="form-row">
              <div className="form-group col-md-6">
                <InputContainer>
                  <UserSVG width={24} height={24} />
                  <input type="text" placeholder="* Nome Completo" required
                    value={this.state.name} onChange={e => this.setState({ form: { name: e.target.value }})} />
                </InputContainer>
              </div>
              <div className="form-group col-md-6">
                <InputContainer>
                  {!this.state.isValidEmail ? <DirectSVG width={24} height={24} /> : <CheckMarkSVG width={24} height={24} />}
                  <input type="email" placeholder="* E-mail" required
                    value={this.state.email} onChange={(e) => this.handleChangeEmail(e)} />
                </InputContainer>
              </div>
            </div>
            <div className="form-row">
              <div className="form-group col-md-6">
                <InputContainer>
                  <PhoneSVG width={24} height={24} />
                  <input type="text" placeholder="Telefone"
                    value={this.state.phone} onChange={e => this.setState({ form: { phone: e.target.value }})} />
                </InputContainer>
              </div>
              <div className="form-group col-md-6">
                <InputContainer>
                  <WhatsappSVG width={24} height={24} />
                  <input type="text" placeholder="Whatsapp"
                    value={this.state.whatsapp} onChange={e => this.setState({ form: { whatsapp: e.target.value }})} />
                </InputContainer>
              </div>
            </div>

            <div class="form-group">
              <label for="exampleFormControlSelect1">Assunto</label>
              <InputContainer className="reset-padding-vertical reset-padding-horizontal">
                <select class="form-control" id="exampleFormControlSelect1"
                  value={this.state.subjectMatter} onChange={e => this.setState({ form: { subjectMatter: e.target.value }})}>
                  <option>Apenas entrar em contato</option>
                  <option>Desejo abrir minha empresa</option>
                  <option>Fechar empresa</option>
                  <option>Descobrir o melhor formato para minha empresa</option>
                  <option>Quero mudar de contador</option>
                </select>
              </InputContainer>
            </div>

            <div className="form-group">
              <InputContainer>
                <textarea rows="4" required placeholder="* Como podemos te ajudar?"
                  value={this.state.message} onChange={e => this.setState({ form: { message: e.target.value }})}></textarea>
              </InputContainer>
            </div>
            <div style={{ display: "flex", justifyContent: "flex-end", marginTop: '30px' }}>
              <button style={{ backgroundColor: colors.primaryColor, padding: '15px 40px', fontSize: '0.85em' }}
                className="btn btn-primary btn-sm mybutton">
                Enviar mensagem
              </button>
            </div>
          </FormRequest>
        </div>
        <div style={{ position: "absolute", zIndex: 9999999999 }}>
          <ToastContainer align="right" position="bottom" />
        </div>
      </div>
    )
  }
}

const FormTitleContainer = styled.div`
  font-family: Montserrat;
  margin-bottom: 70px;
  h5 {
    color: #2f314a;
    font-weight: bold;
    font-size: 1.4em;
  }
  p {
    position: relative;
    font-size: 1.2em;
    font-weight: 400;
    color: #2f314a;
    bottom: 5px;
  }
`
const FormRequest = styled.form`
  background-color: #f7f8ff;
  border-radius: 10px;
  padding: 30px 15px;
  @media(min-width: 600px) {
    padding: 30px 50px;
  }
`
const InputContainer = styled.div`
  display: flex;
  align-items: center;
  background-color: white;
  border: 2px solid #d1d1f3;
  padding: 10px 12px;
  border-radius: 5px;
`
