import React from 'react';
import styled from 'styled-components'
import colors from '../app/colors';

export default function Services() {

  return (
    <React.Fragment>

      <TitleContainer>
        <ServicesTitle><span style={{ fontWeight: 'bold' }}>Soluções completas</span></ServicesTitle>
        <ServicesSubTitle>Apenas alguns dos muitos serviços que podemos fazer por você</ServicesSubTitle>
      </TitleContainer>

      <Container>

        <ServiceItems className='container-fluid'>
          <div className='row'>

            <div className='col-sm-12 col-md-4'>
              <ServiceBox>
                <ServiceBoxImageContainer>
                  <ServiceBoxImage src='https://www.flaticon.com/svg/static/icons/svg/3459/3459834.svg' />
                </ServiceBoxImageContainer>
                <ServideBoxTitle>Serviços contábeis em geral</ServideBoxTitle>
                <ServideBoxText>
                  Administramos toda parte financeira e tributária, o objetivo é tornar o mais simples possível para você
                </ServideBoxText>
              </ServiceBox>
            </div>

            <div className='col-sm-12 col-md-4'>
              <ServiceBox>
                <ServiceBoxImageContainer>
                  <ServiceBoxImage src='https://www.flaticon.com/svg/static/icons/svg/3459/3459637.svg' />
                </ServiceBoxImageContainer>
                <ServideBoxTitle>Microempreendedor individual (MEI)</ServideBoxTitle>
                <ServideBoxText>
                  Abertura gratuita de seu negócio e orientação nos processos
                </ServideBoxText>
              </ServiceBox>
            </div>

            <div className='col-sm-12 col-md-4'>
              <ServiceBox>
                <ServiceBoxImageContainer>
                  <ServiceBoxImage src='https://www.flaticon.com/svg/static/icons/svg/3460/3460111.svg' />
                </ServiceBoxImageContainer>
                <ServideBoxTitle>Parcelamento de tributos</ServideBoxTitle>
                <ServideBoxText>
                  Aliviamos a carga tríbutaria, dividindo em varias parcelas
                </ServideBoxText>
              </ServiceBox>
            </div>

          </div>
        </ServiceItems>

        <ServiceItems className='container-fluid'>
          <div className='row'>

            <div className='col-sm-12 col-md-4'>
              <ServiceBox>
                <ServiceBoxImageContainer>
                  <ServiceBoxImage src='https://www.flaticon.com/svg/static/icons/svg/3460/3460106.svg' />
                </ServiceBoxImageContainer>
                <ServideBoxTitle>Abertura e encerramento de empresas</ServideBoxTitle>
                <ServideBoxText>
                  Abrimos sua empresa sem burocracia, pague apenas as taxas do governo e faça os protocolos físicos,
                  o resto é com a gente
                </ServideBoxText>
              </ServiceBox>
            </div>

            <div className='col-sm-12 col-md-4'>
              <ServiceBox>
                <ServiceBoxImageContainer>
                  <ServiceBoxImage src='https://www.flaticon.com/svg/static/icons/svg/3459/3459877.svg' />
                </ServiceBoxImageContainer>
                <ServideBoxTitle>Departamento fiscal tributária</ServideBoxTitle>
                <ServideBoxText>
                  Fazemos um planejamento tributário que é capaz de reduzir a carga tributária de sua empresa
                </ServideBoxText>
              </ServiceBox>
            </div>

            <div className='col-sm-12 col-md-4'>
              <ServiceBox>
                <ServiceBoxImageContainer>
                  <ServiceBoxImage src='https://www.flaticon.com/svg/static/icons/svg/2698/2698198.svg' />
                </ServiceBoxImageContainer>
                <ServideBoxTitle>Imposto de renda pessoa física</ServideBoxTitle>
                <ServideBoxText>
                  Nossos fazemos o preenchimento e a transmissão de sua declaração
                </ServideBoxText>
              </ServiceBox>
            </div>

          </div>
        </ServiceItems>

      </Container>
    </React.Fragment>

  )
}

const Container = styled.div`
  position: relative;
  background-color: #f2f7fb;
  padding: 30px 0px 30px 0px;
  /* box-shadow: 0 8px 14px rgba(0,0,0,.1); */
  /* border-bottom: 1px solid rgba(0,0,0,.1); */
  @media(min-width: 600px) {
    /* margin: 0% 15%; */
    padding: 50px 30vh;
  }
`

const TitleContainer = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  flex-direction: column;
  margin-bottom: 50px;
  margin-top: 25px;
  @media(min-width: 600px) {
    margin-bottom: 100px;
    margin-top: 100px;
  }
`

const ServiceBoxBackground = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: flex-start;
  margin-top: 15px;
  border-radius: 5px;
  background-size: cover;
  @media(min-width: 600px) {
  }
`

const ServiceBox = styled.div`
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  /* background: rgba(0,0,0,0.7); */
  padding: 40px 30px;
  height: 100%;
  width: 100%;
  border-radius: 5px;
  transition-duration: 0.4s;
  @media(min-width: 600px) {
    padding: 40px 30px;
  }
`

const ServiceItems = styled.div`
  display: flex;
  flex-direction: column;
  @media(min-width: 600px) {
    flex-direction: row;
    justify-content: center;
    align-items: center;
    width: 100%;
  }
`

const ServicesTitle = styled.h4`
  color: #2f314a;
  font-family: 'Montserrat', sans-serif;
  font-weight: 600;
  font-size: 1.6em;
  text-align: center;
  @media(min-width: 600px) {
    font-size: 2em;
  }
`

const ServicesSubTitle = styled.p`
  color: #a3a9b9;
  font-family: 'Montserrat', sans-serif;
  font-weight: 500;
  text-align: center;
  font-size: 1em;
`
const ServideBoxTitle = styled.p`
  color: rgba(0,0,25,1);
  font-family: 'Poppins';
  font-weight: 600;
  font-size: 1.2em;
  /* text-shadow: 1px 1px rgba(0,0,0,.8); */
  @media(min-width: 600px) {
    font-size: 0.9em;
  }
`
const ServideBoxText = styled.p`
  color: #6b7c93;
  /* color: rgba(0,0,25,1); */
  font-family: 'Poppins';
  font-weight: 400;
  margin-top: 0px;
  font-size: 0.95em;
`

const ServiceBoxImageContainer = styled.div`
  background-color: white;
  border-radius: 20px;
  margin-bottom: 15px;
  padding: 10px;
  z-index: 99999;
`

const ServiceBoxImage = styled.img`
  width: 30px;
  height: 30px;
  border-radius: 100px;
`
